__global__ void nussinovCuda(int * table, char * sequence,int * len1, int j, int numBlocks) {
	
	int len = *len1;

	int begin = blockIdx.x; // threadIdx.x;
    int end = blockIdx.x;
	int blockLen =  (len- j) / numBlocks;
    if (blockIdx.x == numBlocks - 1)
    {
        begin = begin * blockLen;
        blockLen += ( len - j ) - blockLen * numBlocks;
        end = begin + blockLen;
    }
    else
    {
        begin = begin * blockLen;
        end = begin + blockLen;
    }
    
    
	// printf("%d\t%d\t%d\t%d\t%d\t%d\t%d\n",begin,end, blockLen,j,blockIdx.x,numBlocks,len);

	auto j0 = j;
    if (blockLen!=0)
	for(int i=begin; i<end; i++)
	{
		int j = i+j0;
		if (i<j)
		{
			int m1 = table[(len*i)+(j-1)];
			int m2 = table[(len*(i+1))+j];
			int m3;
			if(match(sequence,i,j) )
				m3 = table[(len*(i+1))+(j-1)] + match(sequence,i,j);
			else
				m3 = 0;
			int m4 = 0;
            // we are going to use threads in here
			for (int k = i+1;k<j;k++)
			{
				if (table[len * i + k ] + table[ len * j + k + 1 ] > m4)
					m4 = table[len * i + k ] + table[ len * j + (k+1)];
			}
			//cout << m1 << " " << m2 << " " << m3 << " " << m4 << endl;
			// __vmaxu4 ( unsigned int  a, unsigned int  b )
			

			int max =(m3<m4) ? m4 : m3;
			max = (max<m1) ? m1 : max;
			max = (max<m2) ? m2 : max;
			// transposed case
			table[len*j+i] = max;
			// normal case
			table[len*i+j] = max;
			//printf ("%d\t%d\t%d\t%d\n", table[len*j+i], table[len*i+j],len*j+i,len*i+j);
		}
	}
	__syncthreads();
	// printf("Done one iteration.\n");
}

